import React, { useState } from "react";
import "./ExpenseFilter.css";

const ExpensesFilter = (props) => {
  const [enteredYear, setEnteredYear] = useState("");

  /* function yearChangeHandler(opcja) {
    enteredYear = value.opcja;
  } */
  
  function selectHandler(opcja) {
    setEnteredYear(opcja.target.value);
    opcja.preventDefault(enteredYear);
    
    const Filter = {
      Year: enteredYear,
    };
    props.onSaveFilter(Filter);
  }

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select value={enteredYear} onChange={selectHandler}>
          <option value="2022">2022</option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
