import React from "react";
import "./ExpenseFilter.css";

const ExpensesFilter = () => {
    const [enteredYear, setEnteredYear] = useState("");

    function yearChangeHandler(opcja){
        setEnteredYear(opcja.value);
    }
    function selectHandler(opcja){
        opcja.preventDefault();

        const Filter = {
            Year: enteredYear,
        }
        props.onSaveFilter(Filter);
        setEnteredYear("");
    }
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onSelect={selectHandler}>
          <option onChange={yearChangeHandler} value="2022">2022</option>
          <option onChange={yearChangeHandler} value="2021">2021</option>
          <option onChange={yearChangeHandler} value="2020">2020</option>
          <option onChange={yearChangeHandler} value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
