import React, { useState } from "react";
import "./NewExpenseForm.css";
import Card from "../UI/Card";

function ExpenseForms(props) {
  const [eneteredTitle, setEnteredTitle] = useState("");
  const [eneteredAmount, setEnteredAmount] = useState("");
  const [eneteredDate, setEnteredDate] = useState("");

  function titleChangeHandler(event) {
    setEnteredTitle(event.target.value);
  }

  function amountChangeHandler(event) {
    setEnteredAmount(event.target.value);
  }

  function DateChangeHandler(event) {
    setEnteredDate(event.target.value);
  }

  function submitHandler(event) {
    event.preventDefault();

    const expenseData = {
      title: eneteredTitle,
      amount: eneteredAmount,
      date: new Date(eneteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  }

  const [newMenu, setNewMenu] = useState("");
  
    return(
      <div className="new-expense__controls">
        <div className="new-expense__actions">
            <button onClick={() => setNewMenu()}>Add Expense</button>
        </div>
      </div>
    );
  
    
  setNewMenu(function Change(newMenu) {
    return (
      <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              value={eneteredTitle}
              onChange={titleChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Amount</label>
            <input
              value={eneteredAmount}
              type="number"
              min="0.01"
              step="0.01"
              onChange={amountChangeHandler}
            />
          </div>
          <div className="new-expense__control">
            <label>Date</label>
            <input
              value={eneteredDate}
              type="date"
              min="2019-01-01"
              max="2022-12-31"
              onChange={DateChangeHandler}
            />
          </div>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Cancel</button>
          <button type="submit">Add Expense</button>
        </div>
      </form>
    );
  });
}
export default ExpenseForms;
