import React, { useState } from "react";
import "./NewExpenseForm.css";
import ChangeMenu from "../UI/menuExpenses";


function ExpenseForms(props) {
  const [eneteredTitle, setEnteredTitle] = useState("");
  const [eneteredAmount, setEnteredAmount] = useState("");
  const [eneteredDate, setEnteredDate] = useState("");

  function titleChangeHandler(event) {
    setEnteredTitle(event.target.value);
  }

  function amountChangeHandler(event) {
    setEnteredAmount(event.target.value);
  }

  function DateChangeHandler(event) {
    setEnteredDate(event.target.value);
  }

  function submitHandler(event) {
    event.preventDefault();

    const expenseData = {
      title: eneteredTitle,
      amount: eneteredAmount,
      date: new Date(eneteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  }

  const [newMenu, setNewMenu] = useState("");
  
    return(
      <div className="new-expense__controls">
        <div className="new-expense__actions">
            <button onClick={() => setNewMenu(<ChangeMenu/>)}>Add Expense</button>
        </div>
      </div>
    );
}
export default ExpenseForms;
